import React, {Component} from "react";
import API from "../../utils/API";
import Card from "../../components/Card"


class Saved extends Component {

    state = {
        savedResult :[]
    }
    componentDidMount(){
        API.getAllSavedBooks()
        .then(res => {
            console.log(res)
            this.setState({savedResult:res.data})
    }).catch(err=>console.log(err))
   }

   displayResult=()=>{
       return(
           this.state.savedResult.map(elem=>{
               return(

                   <Card result={elem}/>
               )
           })
       )

   }

    render(){
        return(
            <div className="container">
                {this.state.savedResult.length?
                    this.displayResult

                :<div/>}
            </div>
        )

    }

}

export default Saved;
