import React, { Component } from "react";
import API from "../utils/API";
// import Container from "../components/Container";
// import SearchForm from "../components/SearchForm/SearchForm";
import SearchResults from "../components/SearchResults/SearchResults";


class Search extends Component {
  state = {
    book: "",
    Author:"",
    results: [],

  };

  // When the component mounts, get a list of all available base breeds and update this.state.breeds
  componentDidMount() {

  }

  handleInputChange = event => {
    this.setState({ search: event.target.value });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.getBooks(this.state.search)
      .then(res => {
        if (res.data.status === "error") {
          throw new Error(res.data.message);
        }
        this.setState({ results: res.data.message });
      })
      .catch(err => console.log(err) );
  };
  render() {
    return (
      <div>
        <Jumbotron />
        <div className="container" style={{ minHeight: "80%" }}>
          <h1 className="text-center">Search By book name!</h1>
          <form>
            <input name ="book" value={this.state.book} onChange={this.handleInputChange} placeholder="Book Search"/>
            <input name ="author" value={this.state.book} onChange={this.handleInputChange} placeholder="Author Search"/>
          </form>

          <SearchResults results={this.state.results} />
        </div>
      </div>
    );
  }
}

export default Search;
