import React from "react";
import CardBtn from "../CardBtn";
import "./Card.css";

const Card = (props) => (

  <div className="card">
    <div className="card-header">
      <h4 className="mt-2"><a href={props.link}>{props.title}</a></h4>
    </div>
    <h5 className="mt-4">Author: {props.author}</h5>
    <p><img src={props.thumbnail} alt="book" className="pr-3 float-left"/>{props.synopsis}</p>
    <button className="btn btn-view" onClick={()=>this.handleDeleteBtn(props.id)}>Delete</button>
    <a className="btn btn-view" href={props.link}>View</a>

  </div>
);

export default Card;
