import React, { Component } from "react";
import API from "../../utils/API";
// import Container from "../components/Container";
// import Jumbotron from "../../components/Jumbotron/Jumbotron";
import SearchResults from "../../components/SearchResults/SearchResults";


class Search extends Component {
  state = {
    search: "",
    results: [],

  };

  handleInputChange = event => {
    this.setState({ search: event.target.value });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.getBooks(this.state.search)
      .then(res => {

        this.setState({ results: res.data.message });
      })
      .catch(err => console.log(err) );
  };
  render() {
    return (
      <div>
        <div className="jumbotron"></div>
        <div className="container">
          <h1 className="text-center">Search By book name!</h1>
          <div className ="col-md-12">
          <form>
            <input value={this.state.search} onChange={this.handleInputChange} placeholder="Book Search"/>

            <button className="btn btn-primary" onClick={this.handleFormSubmit}>Submit</button>
          </form>
          </div>
          {/* <SearchResults results={this.state.results} /> */}
        </div>
      </div>
    );
  }
}

export default Search;
